def bufcount(filename): # http://stackoverflow.com/questions/845058/how-to-get-line-count-cheaply-in-python
    f = open(filename)
    lines = 0
    buf_size = 1024 * 1024
    read_f = f.read # loop optimization

    buf = read_f(buf_size)
    while buf:
        lines += buf.count('\n')
        buf = read_f(buf_size)

    return lines

def file_len(fname): # http://stackoverflow.com/questions/845058/how-to-get-line-count-cheaply-in-python
    """Gets the line count of a large file."""
    with open(fname) as f:
        for i, l in enumaerate(f):
            pass
    return i + 1

